import React from 'react';
import '../index.css';
import logo from '../logo.svg';


class Login extends React.Component
{
    constructor(props){
        super(props);
        this.state = {value: '', password: ''};


        //ensuring all state changes and actions are controlled by REACT as html elements habve theirs
        this.watchChange = this.watchChange.bind(this);
        this.watchSubmit = this.watchSubmit.bind(this);
    }
        watchChange(event) {
            this.setState({value: event.target.value});
        }
        watchSubmit(event) {
            event.preventDefault();
            alert("Username: " + this.state.value);
            //write own handle for submit
        }

    render(){
        return(
            <div class="row" >
                <div >
                    <img src={logo} className="App-logo" alt="logo" />
                    <p>
                    Edit <code>src/App.js</code> and save to reload.
                    </p>
                    <a
                    className="App-link"
                    href="https://reactjs.org"
                    target="_blank"
                    rel="noopener noreferrer"
                    >
                    Learn React
                    </a>
            </div>
                <div class="col-lg-6 col-md-6 form-group" style={{minWidth: "400px"}} >
                    <form method="post" id="login_form" name="login" onSubmit={this.watchSubmit}>
                        <label>Username: </label>
                        <input class="form-control" type="text" value={this.state.value} name="username" onChange = {this.watchChange} />
                        <label>Password:</label>
                        <input class="form-control" type="password" value={this.state.password} name="password" />

                        <button class="btn btn-primary"> Submit </button>

                    </form>
                </div>
            </div>

        );
    }
}

export default Login;
